
public class Swapping {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a=10;
		int b=20;
		System.out.println("Enter the value of a before swapping a,b : "+a+","+b);

		a=a+b;//30
		b=a-b;//30-20 =10
		a=a-b; // 30-10=20
		System.out.println(" Enter the value of a before swapping a,b : "+a+","+b);
	}

}
